package capgeminibasics;

public class unaryopertor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=1;
a++;
System.out.println(a);
a--;
System.out.println(a);

//postincrement
int b=5;
System.out.println(b++);
System.out.println(b);


//preincrement
int c =10;
System.out.println(++c);
System.out.println(c);

int d =20;
System.out.println(--d);
System.out.println(d);
	}

}
